import React from 'react'

function ClearFields() {
    document.getElementById("input").value = ""
}

const Questionaire = ({
    showAnswers,
    setShowAnswers,
    handleAnswer, 
    handleNextQuestion,
    setInput,
    input,
    setMessage,
    message,
    quote,
    song,
}) => {
    return (
    <div className='flex flex-col'>
        <div className='bg-white text-purple-800 p-10 rounded-lg shadow-md'>
            <h1>What song is this from?</h1>
            <h2 className='text-2xl' dangerouslySetInnerHTML={{ __html: quote}} />

            <input
            id="input" 
            style={{ border: '2px solid', borderColor: 'purple', padding: '3px', marginRight: '6px', borderRadius: '5px' }} 
            type="text" onChange={(e) => {
                setInput(e.target.value)
            }} />

            <button type="submit" style={{ border: '2px solid', borderColor: 'purple', padding: '3px', borderRadius: '5px' }} onClick={() => {
                setShowAnswers(true)
                ClearFields()

                if (input === song){
                    setMessage("Correct!")
                } else {
                    setMessage(`Wrong! Song was: ${song} Your answer: ${input}`)
                }
            }}>Submit</button>
            <p>{message}</p>
        </div>


        {showAnswers && (
        <button onClick={(async ()=>{
            setMessage("")
            handleNextQuestion(false)
        })} 
            className={`ml-auto mt-6 bg-purple-700 text-white p-4 font-semibold rounded shadow`} >
            Next Question</button>
        )}


    </div>
)
};


export default Questionaire;
