import React from 'react'

const Questionaire = ({
    showAnswers,
    handleAnswer, 
    handleNextQuestion,
    setInput,
    setMessage,
    //data: {quote, song, answer},
    quote,
    song,
    answer,
}) => {
    return (
    <div className='flex flex-col'>
        <div className='bg-white text-purple-800 p-10 rounded-lg shadow-md'>
            <h1>What song is this from?</h1>
            <h2 className='text-2xl' dangerouslySetInnerHTML={{ __html: quote}} />
            <input style={{ border: '2px solid', borderColor: 'light-purple', padding: '3px', marginRight: '6px' }} type="text" onChange={(e) => {
                setInput(e.target.value)
            }} />

            <button style={{ border: '2px solid', borderColor: 'light-purple', padding: '3px' }} onClick={() => {
                if (answer === song){
                    setMessage("Correct!")
                } else {
                    setMessage("Wrong! Song was: ", {song})
                }
            }}>Submit</button>
        </div>


        { /* <div className='grid grid-cols-2 gap-6 mt-6'>
            {answer.map(answer => {
                const textColor = showAnswers ? answer === song
                    ? 'text-green-500' 
                    : 'text-red-500' 
                : 'text-purple-700';

                // const textColor = showAnswers ? 'text-white' : 'text-purple-800';
                return (
                <button
                    className={`bg-white ${textColor} p-4 font-semibold rounded shadow`}
                    onClick={() => handleAnswer(answer)} dangerouslySetInnerHTML={{ __html: answer}} />
            )})}
        </div> */}
        {showAnswers && (   
            <button 
            onClick={handleNextQuestion}
            className={`ml-auto mt-6 bg-purple-700 text-white p-4 font-semibold rounded shadow`}>Next Question</button>

        )}
    </div>
)};


export default Questionaire;
