import React, { useState, useEffect } from 'react'

const csv = require('csvtojson');
const path = require('path');
const csvFilePath = path.join(__dirname, 'taylor_swift_lyrics.csv');

const API_URL = 'https://taylorswiftapi.herokuapp.com/get';

const csvToJson = async () => {
  const songsArray = await csv().fromFile(csvFilePath);

  // select first 10 songs from the json array
  for (var i = 0; i < 10; ++i){
      console.log(songsArray[i]);
  }

  // todo: The sequence is up to you
};

function App() {
  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    fetch(API_URL)
      .then(res => res.json())
      .then(data => {
        console.log(data)
      })
  }, [])

  return (
    <div className="container">
      <div className='bg-white text-purple-800 p-10 rounded-lg shadow-md'>
        <h2 className='text-2xl'>
          1. This is where we're going to have the question?
        </h2>
      </div>
      <div className='grid grid-cols-2 gap-6 mt-6'>
        <button className='bg-white p-4 text-purple-800 font-semibold rounded shadow'>
          Answer 1
        </button>
        <button className='bg-white p-4 text-purple-800 font-semibold rounded shadow'>
          Answer 2
        </button>
        <button className='bg-white p-4 text-purple-800 font-semibold rounded shadow'>
          Answer 3
        </button>
        <button className='bg-white p-4 text-purple-800 font-semibold rounded shadow'>
          Answer 4</button>
      </div>
    </div>
  );
}

export default App;
