import React from 'react'

const Questionaire = ({
    showAnswers,
    setShowAnswers,
    handleAnswer, 
    handleNextQuestion,
    setInput,
    input,
    setMessage,
    message,
    quote,
    song,
}) => {
    return (
    <div className='flex flex-col'>
        <div className='bg-white text-purple-800 p-10 rounded-lg shadow-md'>
            <h1>What song is this from?</h1>
            <h2 className='text-2xl' dangerouslySetInnerHTML={{ __html: quote}} />

            <input 
            style={{ border: '2px solid', borderColor: 'purple', padding: '3px', marginRight: '6px', borderRadius: '5px' }} 
            type="text" onChange={(e) => {
                setInput(e.target.value)
            }} />

            <button style={{ border: '2px solid', borderColor: 'purple', padding: '3px', borderRadius: '5px' }} onClick={() => {
                setShowAnswers(true)

                if (input === song){
                    setMessage("Correct!")
                } else {
                    setMessage(`Wrong! Song was: ${song}`)
                }
            }}>Submit</button>

            {/* <button onClick={async ()=>{
                setMessage("")
            }}></button> */}
            <p>{message}</p>
        </div>


        { /* <div className='grid grid-cols-2 gap-6 mt-6'>
            {answer.map(answer => {
                const textColor = showAnswers ? answer === song
                    ? 'text-green-500' 
                    : 'text-red-500' 
                : 'text-purple-700';

                // const textColor = showAnswers ? 'text-white' : 'text-purple-800';
                return (
                <button
                    className={`bg-white ${textColor} p-4 font-semibold rounded shadow`}
                    onClick={() => handleAnswer(answer)} dangerouslySetInnerHTML={{ __html: answer}} />
            )})}
        </div> */}


        {showAnswers && (
        <button onClick={(async ()=>{
            setMessage("") && (handleNextQuestion(false) && setShowAnswers(false))
        })} 
            className={`ml-auto mt-6 bg-purple-700 text-white p-4 font-semibold rounded shadow`} >
            Next Question</button>
        )}


    </div>
)};


export default Questionaire;
